import React,{useState,useEffect} from 'react'
import './Row.css';
import axios from './axios';


function Row({title,fetchURL,Poster=false}) {

    const [movies,setMovies] = useState([]);

    const baseURL = "https://image.tmdb.org/t/p/original/";

    useEffect(() => {
        async function fetchData(){
            const request = await axios.get(fetchURL);
            setMovies(request.data.results);
            return request;
        }
        fetchData();
    }, [fetchURL]);

    console.log(movies);
    return (
        <div className="row">
            <h2>{title}</h2>
            {movies.map( (movie) => (
                <img className={`backdrop && ${Poster && "poster"}`}
                src={`${baseURL}${Poster?movie.poster_path:movie.backdrop_path}`}
                alt={movie.name} />
            ))}
        </div>
    )
}

export default Row
