import React,{ useState,useEffect } from 'react'
import './Banner.css'
import axios from './axios';
import requests from './Request_fetch';


function Banner() {

    const [movie,SetMovie] = useState([]);
    const [link,Setlink] = useState("https://upload.wikimedia.org/wikipedia/commons/thumb/c/cd/Black_flag.svg/1200px-Black_flag.svg.png");

    useEffect( () =>{
        async function fetchMovie(){
            const request = await axios.get(requests.ComedyMovies);
            SetMovie(
                request.data.results[Math.floor(Math.random()*request.data.results.length - 1)]
            );
        return request;
        }
        fetchMovie();
    },[])
    if(movie && movie.backdrop_path)
            {
                Setlink(`https://image.tmbd.org/t/p/original/${movie.backdrop_path}`);
            }

    console.log(movie);

    const truncate_description = (str,n) => {
        if(str && str.length>n)
            return str.substr(0,n)+'...';
        else
            return str;
    }
    return (
        <header className="banner" style={{
            backgroundSize: "cover",
            backgroundImage: `url(${link})`,
            backgroundPosition: "center center",
        }}>
        <div className="content">
            <h1 className="title">Movie</h1>
            <div className="buttons">
                <button className="button">Play</button>
                <button className="button">My List</button>
            </div>
            <h1 className="description">
                {truncate_description("hdaji bsadjsaj jbdasjaisjda sjdaibjdbasbda sdja",200)}
            </h1>
        </div>
            <div className="fade">

            </div>
        </header>
    )
}

export default Banner
